/**
 * Get publications by category
 */
export function getPublicationsByCategory(category: string): VerifiedPublication[] {
  return verifiedPublications.filter(pub => 
    pub.categories.includes(category.toLowerCase())
  );
}

/**
 * Get publications by multiple categories (OR logic)
 */
export function getPublicationsByCategories(categories: string[]): VerifiedPublication[] {
  const lowerCategories = categories.map(c => c.toLowerCase());
  return verifiedPublications.filter(pub => 
    pub.categories.some(cat => lowerCategories.includes(cat))
  );
}

/**
 * Search publications by author, title, or journal
 */
export function searchPublications(query: string): VerifiedPublication[] {
  const lowerQuery = query.toLowerCase();
  return verifiedPublications.filter(pub =>
    pub.authors.toLowerCase().includes(lowerQuery) ||
    pub.title.toLowerCase().includes(lowerQuery) ||
    pub.journal.toLowerCase().includes(lowerQuery)
  );
}

// Disease/Area-specific publication collections for individual pages
// These map to specific page needs

export const neurosciencePublications = verifiedPublications.filter(p => 
  p.categories.includes('neuroscience')
);

export const oncologyPublications = verifiedPublications.filter(p => 
  p.categories.includes('oncology')
);

export const immunologyPublications = verifiedPublications.filter(p => 
  p.categories.includes('immunology')
);

export const cardiovascularPublications = verifiedPublications.filter(p => 
  p.categories.includes('cardiovascular')
);

export const methodsPublications = verifiedPublications.filter(p => 
  p.categories.includes('methods') || p.categories.includes('cre-lox')
);
